import React from "react";
import { Link } from "react-router-dom";
import "./header.scss";

const Header = () => {
  const basketItem = 2;
  return (

    <header>
      <div class="navigation">
        <input type="checkbox" className="navigation__checkbox" id="navi-toggle" />
        <label for="navi-toggle" className="navigation__button">
          <div className="navigation__icon">&nbsp;</div>
        </label>

        <nav className="navigation__nav">
          <ul className="navigation__list">
            <li className="navigation__item"><a href="index.html" className="Home">Home</a></li>
            <li className="navigation__item"><a href="projects.html" className="Shop">Projects</a></li>
            <li className="navigation__item"><a href="Media.html" className="Contactk">About Us</a></li>
          </ul>
        </nav>
      </div>

      <img className="logo" src="/img/logo1.png" alt="" />
      <input className="search-bar" type="search" />
      <button className="btn">
        <Link className="btn-text" to="/">
          search
        </Link>
      </button>
      <div className="icon-box">
        {/* <Link className="text" to="sign-in.html">
          {" "}
          login/register
        </Link> */}
        {/* <div className="icon icon-1 "></div> */}
        <div className="icon-text">
          <Link to="#cart-popup">Cart</Link>
        </div>
        <div className="icon icon-2"></div>
        {basketItem > 0 && (
          <div className="cart-amount">
            <div className="cart-figure">{basketItem}</div>
          </div>
        )}
      </div>

    </header>



  );
};

export default Header;
